defmodule Fixtures.Text do
  @moduledoc ~S"""
  Text related generators.
  """
  use Fixtures.Helper

  @charset "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789" |> String.split("")

  @doc ~S"""
  Generate a random string.

  ## Options

   - `:length` the length of the generated string. Allows range of length.
   - `:charset` a string with all allowed characters.
  """
  @spec string(Keyword.t()) :: String.t()
  def string(opts \\ []) do
    range =
      case opts[:length] || 16 do
        a..b -> a..b
        a -> a..a
      end

    cs =
      case opts[:charset] do
        nil -> @charset
        custom -> String.split(custom, "")
      end

    gen_string(Enum.random(range), cs)
  end

  @spec gen_string(integer, String.t(), String.t()) :: String.t()
  defp gen_string(left, charset, acc \\ [])
  defp gen_string(0, _charset, acc), do: :binary.list_to_bin(acc)

  defp gen_string(left, charset, acc),
    do: gen_string(left - 1, charset, [Enum.random(charset) | acc])

